mapper.insert:
/* insertCommonCode */
INSERT INTO common_code(
	code_key, code_name, code_name_en, code_type, code_value, use_yn, view_order, css_class, image, description
) values(
	#{code_key}, #{code_name}, #{code_name_en}, #{code_type}, #{code_value}, #{use_yn}, #{view_order}, #{css_class}, #{image}, #{description}
)

mapper.update:
UPDATE common_code
SET code_name = #{code_name}

	, code_value = #{code_value}


	, code_name_en = #{code_name_en}


	, code_type = #{code_type}


	, use_yn = #{use_yn}


	, view_order = #{view_order}


	, css_class = #{css_class}


	, image = #{image}


	, description = #{description}

WHERE code_key = #{code_key} AND code_value = #{db_code_value}
